const BaseModal = () => {
  const [open, setOpen] = useState(false)
  return (
    <DialogRoot {...root} open={open} onOpenChange={({ open }) => setOpen(open)}>
      <DialogPortal>
        <DialogBackdrop />
        <DialogPositioner>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Reset Password</DialogTitle>
            </DialogHeader>
            <DialogDescription>Enter your email address and we will send you a link to reset your password.</DialogDescription>
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
              <img width="160" height="160" src={AskingQuestionImage} alt="" />
            </div>
            <DialogFooter>
              <Button theme="secondary" onClick={() => setOpen(false)}>取消</Button>
              <Button>按鈕</Button>
            </DialogFooter>
          </DialogContent>
        </DialogPositioner>
      </DialogPortal>
    </DialogRoot>
  )
}
